mapper.update:
UPDATE users
SET username = #{username}, password = #{password}, salt = #{salt}, phone = #{phone}, email = #{email},
    avatar_url = #{avatarUrl}, signature = #{signature}, profile = #{profile}, role = #{role}, created_at = #{createdAt}, updated_at = #{updatedAt}
WHERE id = #{id}

mapper.select:
SELECT * FROM users


        AND username LIKE CONCAT('%', #{username}, '%')


        AND phone = #{phone}


        AND email = #{email}


        AND role = #{role}


        AND created_at >= #{createdAt}
